sp_widget.client_script:
api.controller = function($scope, $timeout) {
  var c = this;
  c.success = null;
  c.error = null;

  c.cancel = function(attendeeId) {
    c.server.get({ action: 'cancel', attendee_id: attendeeId }).then(function(response) {
      c.success = response.data.success;
      c.error = response.data.error;
      c.reload();
    });
  };

  c.view = function(attendeeId) {
    // You can show a modal, navigate to another page, or use GlideModal â€” for now just alert
    alert('Viewing details for attendee: ' + attendeeId);
  };

  c.reload = function() {
    $timeout(function() {
      c.server.get().then(function(response) {
        $scope.data = response.data;
      });
    }, 400);
  };
};

sp_widget.script:
(function() {
  data.attendees = [];
  data.success = '';
  data.error = '';

  if (input && input.action === 'cancel' && input.attendee_id) {
    var att = new GlideRecord('x_1539926_event_ma_attendee');
    if (att.get(input.attendee_id)) {
      att.setValue('x_status', 'Cancelled');
      att.update();
      data.success = 'Attendee registration cancelled.';
    } else {
      data.error = 'Attendee not found.';
    }
  }

  var gr = new GlideRecord('x_1539926_event_ma_attendee');
//   gr.addQuery('x.event_id.x_status', '!=', 'cancelled');
  gr.query();

  while (gr.next()) {
    data.attendees.push({
      sys_id: gr.getUniqueValue(),
      number: gr.getValue('number'),
      first_name: gr.getValue('x_first_name'),
      last_name: gr.getValue('x_last_name'),
      email: gr.getValue('x_email'),
      contact: gr.getValue('contact_number'),
      event: gr.getDisplayValue('x_event_id')
    });
  }
})();
