sp_widget.client_script:
api.controller = function($scope, $timeout) {
  var c = this;
  c.success = null;
  c.error = null;

  c.approve = function(id) {
    c.server.get({ action: 'approve', id: id }).then(function(response) {
      c.success = response.data.success;
      c.error = response.data.error;
      c.reload();
    });
  };

  c.deny = function(id) {
    c.server.get({ action: 'deny', id: id }).then(function(response) {
      c.success = response.data.success;
      c.error = response.data.error;
      c.reload();
    });
  };

  c.reload = function() {
    $timeout(function() {
      c.server.get().then(function(response) {
        $scope.data = response.data;
      });
    }, 400);
  };
};

sp_widget.script:
(function() {
  data.success = '';
  data.error = '';
  data.bookings = [];

  if (input && input.action && input.id) {
    var vr = new GlideRecord('x_1539926_event_ma_venue');
    if (vr.get(input.id)) {
      if (input.action === 'approve') {
        vr.setValue('x_booking_status', 'approved');
        vr.update();
        data.success = 'Venue approved.';
      } else if (input.action === 'deny') {
        vr.setValue('x_booking_status', 'not approved');
        vr.update();
        data.success = 'Venue denied.';
      }
    } else {
      data.error = 'Booking not found.';
    }
  }

  var gr = new GlideRecord('x_1539926_event_ma_venue');
  gr.orderByDesc('sys_created_on');
  gr.query();
  while (gr.next()) {
    data.bookings.push({
      sys_id: gr.getUniqueValue(),
      number: gr.getValue('number'),
      name: gr.getValue('x_venue_name'),
      location: gr.getValue('x_location'),
      capacity: gr.getValue('x_capacity'),
      contact_person: gr.getValue('x_contact_person'),
      contact_number: gr.getValue('contact_number'),
      //event: gr.getDisplayValue('x_event'),
      status: gr.getValue('x_booking_status')
    });
  }
})();
