sp_widget.client_script:
api.controller = function($scope, $timeout) {
  var c = this;
  c.success = null;
  c.error = null;

  c.complete = function(taskId) {
    c.server.get({ action: 'complete', task_id: taskId }).then(function(response) {
      $scope.data = response.data;
      c.success = response.data.success;
      c.error = response.data.error;
    });
  };

  c.cancel = function(taskId) {
    c.server.get({ action: 'cancel', task_id: taskId }).then(function(response) {
      $scope.data = response.data;
      c.success = response.data.success;
      c.error = response.data.error;
    });
  };
};

sp_widget.css:
h5 {
  font-size: 16px;
  font-weight: bold;
}

.card, .border {
  font-size: 13px;
  line-height: 1.3;
}

.btn-sm {
  font-size: 12px;
}

sp_widget.script:
(function() {
  data.tasks = [];
  data.success = '';
  data.error = '';

  var now = new GlideDateTime().getValue();

  // Handle task action via GET
  if (input && input.action && input.task_id) {
    var task = new GlideRecord('x_1539926_event_ma_event_task');
    if (task.get(input.task_id)) {
      if (input.action === 'complete') {
        task.x_status = 'Completed';
        task.update();
        data.success = 'Task marked as completed.';
      } else if (input.action === 'cancel') {
        task.x_status = 'Cancelled';
        task.update();
        data.success = 'Task cancelled.';
      }
    } else {
      data.error = 'Task not found.';
    }
  }

  // Only fetch active tasks
  var gr = new GlideRecord('x_1539926_event_ma_event_task');
  gr.addQuery('x_status', 'NOT IN', 'Completed,Cancelled');
  gr.query();
  while (gr.next()) {
    data.tasks.push({
      sys_id: gr.getUniqueValue(),
      number: gr.getValue('number'),
      name: gr.getValue('x_task_name'),
      event: gr.getDisplayValue('x_event_id'),
      assigned_to: gr.getDisplayValue('assigned_to'),
      due_date: gr.getDisplayValue('x_due_date'),
      status: gr.getValue('x_status')
    });
  }
})();
