sp_widget.client_script:
api.controller = function($scope, $timeout) {
  var c = this;

  c.success = null;
  c.error = null;

  c.reschedule = function(eventId) {
    var newDate = prompt("Enter new date & time (YYYY-MM-DD HH:mm):");

    if (!newDate) return;
    var dateRegex = /^\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}$/;
    if (!dateRegex.test(newDate)) {
      c.error = "Invalid format. Please use YYYY-MM-DD HH:mm.";
      c.success = null;
      return;
    }

    c.server.get({ action: 'reschedule', event_id: eventId, new_date: newDate }).then(function(response) {
      c.success = response.data.success;
      c.error = response.data.error;
      $scope.data = response.data;
    });
  };

  c.cancel = function(eventId) {
    c.server.get({ action: 'cancel', event_id: eventId }).then(function(response) {
      c.success = response.data.success;
      c.error = response.data.error;
      $scope.data = response.data;
    });
  };
};

sp_widget.script:
(function() {
  data.events = [];
  data.success = '';
  data.error = '';

  var now = new GlideDateTime().getValue();

  if (input && input.action && input.event_id) {
    var eventGR = new GlideRecord('x_1539926_event_ma_event');
    if (eventGR.get(input.event_id)) {

      if (input.action === 'cancel') {
        eventGR.setValue('x_status', 'cancelled');
        eventGR.update();
        data.success = 'Event cancelled successfully.';
      }

      else if (input.action === 'reschedule') {
        if (!input.new_date) {
          data.error = 'New date/time is required.';
        } else {
          // Try to parse input date string into GlideDateTime
          try {
            var gdt = new GlideDateTime();
            gdt.setDisplayValue(input.new_date); // expects format YYYY-MM-DD HH:mm

            // Check if new date is in future
            if (gdt.compareTo(new GlideDateTime()) <= 0) {
              data.error = 'New date/time must be in the future.';
            } else {
              eventGR.setValue('x_event_date', gdt);
              eventGR.setValue('x_status', 'Rescheduled');
              eventGR.update();
              data.success = 'Event rescheduled to ' + gdt.getDisplayValue();
            }
          } catch (ex) {
            data.error = 'Invalid date/time format.';
          }
        }
      }

    } else {
      data.error = 'Event not found.';
    }
  }

  // Fetch upcoming events
  var gr = new GlideRecord('x_1539926_event_ma_event');
  gr.addQuery('x_event_date', '>=', now);
  gr.addQuery('x_status', '!=', 'cancelled');
  gr.query();

  while (gr.next()) {
    data.events.push({
      sys_id: gr.getUniqueValue(),
      name: gr.getValue('x_event_name'),
      date: gr.getDisplayValue('x_event_date'),
      location: gr.getValue('x_event_location')
    });
  }
})();
