sys_variable_value.value:
(function execute(inputs, outputs) {
    var budgetUtils = new BudgetUtils();

    // Calculate total budget for the event number provided as input
    outputs.total_budget = parseFloat(
        budgetUtils.getTotalBudget(inputs.event_number)
    ) || 0;

    // Get detailed budget breakdown as a JSON string
    var details = budgetUtils.getBudgetDetailsByEvent(inputs.event_number);
    outputs.budget_details_json = JSON.stringify(details || []);
})(inputs, outputs);

