sys_script_include.script:
var VendorUtils = Class.create();
VendorUtils.prototype = {
    initialize: function() {
    },

	getVendorById: function(vendorId) {
        var vendorGR = new GlideRecord('x_1539926_event_ma_vendor');
        if (vendorGR.get(vendorId)) {
            return {
                sys_id: vendorGR.getUniqueValue(),
                name: vendorGR.getDisplayValue('x_vendor_name'),
                contact_number: vendorGR.getValue('x_contact_number'),
            };
        }
        return null;
    },

    isVendorAvailable: function(vendorId) {
        var vendorGR = new GlideRecord('x_1539926_event_ma_vendor');
        if (vendorGR.get(vendorId)) {
            return vendorGR.getValue('x_is_available') == 'true';
        }
        return false;
    },


    getVendorsByEvent: function(eventId) {
        var vendors = [];
        var vendorGR = new GlideRecord('x_1539926_event_ma_vendor');
        vendorGR.addQuery('x_event_id.number', eventId);
        vendorGR.query();
        while (vendorGR.next()) {
            vendors.push({
                sys_id: vendorGR.getUniqueValue(),
                name: vendorGR.getDisplayValue('x_vendor_name'),
                available: vendorGR.getValue('x_is_available') == 'true'
            });
        }
        return vendors;
    },


    type: 'VendorUtils'
};
