sys_script_include.script:
var GetUpcomingEvents = Class.create();
GetUpcomingEvents.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getEvents: function() {
        var gr = new GlideRecord('x_1539926_event_ma_event'); 
        gr.addQuery('x_event_date', '>=', gs.nowDateTime());
        gr.orderBy('x_event_date');
        gr.query();

        var results = [];
        while (gr.next()) {
            results.push({
                eventName: gr.getValue('x_event_name'),
                eventDate: gr.getValue('x_event_date'),
                location: gr.getValue('x_location'),
                status: gr.getValue('x_status')
            });
        }
        return JSON.stringify(results);
    },



    type: 'GetUpcomingEvents'
});
