sys_script_include.script:
var AttendeeUtils = Class.create();
AttendeeUtils.prototype = {
    initialize: function() {
    },

	isRegistered: function(attendeeNumber) {
        var gr = new GlideRecord('x_1539926_event_ma_attendee');
        gr.addQuery('number', attendeeNumber);
        gr.query();
        return gr.next(); // true if record exists
    },

	getAttendeesByEvent: function(eventNumber) {

        var eventGR = new GlideRecord('x_1539926_event_ma_event');
        eventGR.addQuery('number', eventNumber);
        eventGR.query();

        if (!eventGR.next()) {
            return []; // event not found
        }

        var attendees = [];
        var gr = new GlideRecord('x_1539926_event_ma_attendee');
        gr.addQuery('x_event_id', eventGR.getUniqueValue());
        gr.query();

        while (gr.next()) {
            attendees.push({
                number: gr.getValue('number'),
                name: gr.getValue('x_first_name'),
                email: gr.getValue('x_email')
            });
        }

        return attendees;
    },

	attendeeDetails : function(attID)
	{
		var grAtt = new GlideRecord('x_1539926_event_ma_attendee');
		grAtt.addQuery('number',attID);
		grAtt.query();
		if(grAtt.next())
		{
			return {
				email:grAtt.getValue('x_email'),
				name:grAtt.getValue('x_first_name')
			}
		}
	},


    type: 'AttendeeUtils'
};
