sys_script_include.script:
var FeedbackUtils = Class.create();
FeedbackUtils.prototype = {
    initialize: function() {
    },

	getFeedbackById: function(feedbackId) {
        var fbGR = new GlideRecord('x_1539926_event_ma_feedback');
        if (fbGR.get(feedbackId)) {
            return {
                sys_id: fbGR.getUniqueValue(),
                event: fbGR.getDisplayValue('x_event_id'),
                rating: fbGR.getValue('x_ratings'),
                comments: fbGR.getValue('x_comments'),
            };
        }
        return null;
    },

isFeedbackPositive: function(feedbackId) {
    var fbGR = new GlideRecord('x_1539926_event_ma_feedback');
    if (fbGR.get(feedbackId)) {
        var rating = fbGR.getValue('x_ratings') || '';
        rating = rating.toLowerCase();

        if (rating === 'good' || rating === 'excellent') {
            return true;
        }
    }
    return false;
}
,

	getFeedbackByEvent: function(eventId) {
        var feedbackList = [];
        var fbGR = new GlideRecord('x_1539926_event_ma_feedback');
        fbGR.addQuery('x_event_id.number', eventId);
        fbGR.query();
        while (fbGR.next()) {
            feedbackList.push({
                sys_id: fbGR.getUniqueValue(),
                rating: fbGR.getValue('x_ratings'),
                comments: fbGR.getValue('x_comments'),
            });
        }
        return feedbackList;
    },

    type: 'FeedbackUtils'
};
