sys_script_include.script:
var BudgetUtils = Class.create();
BudgetUtils.prototype = {
    initialize: function() {
    },


    getTotalBudget: function(eventNumber) {
        var eventGR = new GlideRecord('x_1539926_event_ma_event'); 
        eventGR.addQuery('number', eventNumber);
        eventGR.query();

        if (!eventGR.next()) return 0;

        var total = 0;
        var budgetGR = new GlideRecord('x_1539926_event_ma_budget'); 
        budgetGR.addQuery('x_event_id', eventGR.getUniqueValue()); 
        budgetGR.query();

        while (budgetGR.next()) {
            total += parseFloat(budgetGR.getValue('x_total_budget') || 0);
        }

        return total;
    },


    isBudgetApproved: function(budgetNumber) {
        var gr = new GlideRecord('x_1539926_event_ma_budget'); // Replace with your budget table
        gr.addQuery('number', budgetNumber);
        gr.query();

        if (gr.next()) {
        return gr.getValue('x_approved') == 'true' || gr.approval_status == true;
    }

        return false;
    },


    getBudgetDetailsByEvent: function(eventNumber) {
        var eventGR = new GlideRecord('x_1539926_event_ma_event'); 
        eventGR.addQuery('number', eventNumber);
        eventGR.query();

        if (!eventGR.next()) return [];

        var details = [];
        var gr = new GlideRecord('x_1539926_event_ma_budget');
        gr.addQuery('x_event_id', eventGR.getUniqueValue());
        gr.query();

        while (gr.next()) {
            details.push({
                budgetNumber: gr.getValue('number'),
                allocated: gr.getValue('x_total_budget'),
                spent: gr.getValue('x_amount_spent'),
                approved: gr.getValue('x_approved')
            });
        }

        return details;
    },

    type: 'BudgetUtils'
};
